package aosp.ide.app;

import aosp.ide.list.LinkedList;

import static aosp.ide.utilities.StringUtils.join;
import static aosp.ide.utilities.StringUtils.split;
import static aosp.ide.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
